namespace RapidSoft.VTB24.ArmSecurity
{
    using System;
    using System.Runtime.Serialization;

    public class SecurityCheckException : Exception
    {
        public SecurityCheckException()
        {
        }

        public SecurityCheckException(string message)
            : base(message)
        {
        }

        public SecurityCheckException(string message, Exception inner)
            : base(message, inner)
        {
        }

        protected SecurityCheckException(SerializationInfo info, StreamingContext context)
            : base(info, context)
        {
        }
    }
}